+
+        // いいね機能
+        public function like($id)
+        {
+            Like::create([
+                'article_id'=>$id,
+                'user_id'=>Auth::id(),
+            ]);
+    
+            return redirect()->back();
+        }
+    
+        // いいね解除
+        public function unlike($id)
+        {
+            $like = Like::where('article_id',$id)->where('user_id',Auth::id())->first();
+            $like->delete();
+    
+            return redirect()->back();
+        }
 }
